import {React, useEffect} from "react";
import {useDispatch, useSelector} from "react-redux";
import { getCreditCards, getDebetCards } from "../HttpRequest";
import { setCreditAction, setDebetAction } from "../Redux/Reducers/CardReducer";
export default function Sidebar() {
    const BankAccount = useSelector(state => state.BankAccount.account); 
    const debetCards = useSelector(state => state.Card.debet);
    const creditCards = useSelector(state => state.Card.credit);
    let creditBalance = 0;
    let debetBalance = 0;
    const dispatch = useDispatch();
    useEffect(() => {
        if (debetCards == null || creditCards == null) {
            if(BankAccount != null) {
                getCreditCards(BankAccount?.id)
                .then((res)=> {
                    dispatch(setCreditAction(res));
                    console.log(res);
                })
                getDebetCards(BankAccount?.id)
                .then((res) => {
                    dispatch(setDebetAction(res));
                })
            }
        }
    }, [BankAccount])

    useEffect(() => {
        creditCards?.forEach(element => {
            creditBalance += element.Balance
        });
    },[creditCards])

    useEffect(() => {
        debetCards?.forEach(element => {
            debetBalance += element.Balance
        });
    },[debetCards])

    return (
        <div class="flex-shrink-0 p-3 border-end" style={{width: "280px", height: outerHeight}}>
            <h5>Счета и карты</h5>
            <div>            
                <div class="row">
                    <div class="col">
                    <img width={50} height={50} src={require("../Images/credit.png")}/>
                    </div>
                    <div class="col-8">
                        <p>Мои карты</p>
                        <p>{debetBalance}₽</p>
                    </div>
                </div>
                <ul class="list-unstyled ps-0">
                    {debetCards?.map((dc) => {
                        <li>
                            <div class="row">
                                <div class="col">
                                    <h3 class="text-muted">{dc.Balance}₽</h3>
                                </div>
                                <div class="col">                                
                                    <img width={50} height={50} src={require("../Images/ClientImages/" + dc.Image)}/>
                                </div>                        
                            </div>
                        </li>
                    })
                    }
                </ul>       
            </div>

            <div>
                <div class="row">
                    <div class="col">
                    <img width={50} height={50} src={require("../Images/credit.png")}/>
                    </div>
                    <div class="col-8">
                        <p>Кредитные карты</p>
                        <p>{creditBalance}₽</p>
                    </div>
                </div>
                <ul class="list-unstyled ps-0">
                    {creditCards?.map((cc) => {
                        <li>
                            <div class="row">
                                <div class="col">
                                    <h3 class="text-muted">{cc.Balance}₽</h3>
                                </div>
                                <div class="col">                                
                                    <img width={50} height={50} src={require("../Images/ClientImages/" + cc.Image)}/>
                                </div>                        
                            </div>
                        </li>
                    })
                    }
                </ul>       
            </div>
            
        </div>       
    )
}
