import {React, useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getTransactions } from '../../HttpRequest';
import { setTransactionAction } from '../../Redux/Reducers/TransactionReducer';

export default function Transaction() {
    const transactions = useSelector(state => state.Transaction.transactions);
    const account = useSelector(state => state.BankAccount.account);
    const dispatch = useDispatch();
    useEffect(() => {
        if (account != null) {
            if ( transactions == null) {
                getTransactions(account.id)
                .then ((res) => {
                    dispatch(setTransactionAction(res));
                })
            }            
        }
    },[account])
    
    return(
        <div className='container '>
            <h3>ИСТОРИЯ ОПЕРАЦИЙ</h3>
            <ul className='list-unstyled ps-0'>
                {transactions?.map((el, id) => {
                    return(
                        <li key={id}>
                            <div class="row border-bottom text-center align-items-center" style={{height: "80px"}}>
                                <div class="col">
                                    <b>{el.time}</b>
                                    <p className='text-muted'>{el.day}</p>
                                </div>
                                <div class="col">                                
                                    <img height={50} width={50} alt="Фото"/>                                    
                                </div>     
                                <div class="col">
                                    <p>{el.accountName}</p>
                                </div>
                                <div class="col">
                                    <img height={50} width={50} src={require("../../Images/CardImages/" + el.cardImage)} alt="Фото"/>                                    
                                </div>
                                <div class="col">
                                    <b>{el.carnNumber}</b>                                    
                                </div>
                                <div class="col">
                                    {el.receiver} === {account.id} ??
                                        (
                                            <b>+{el.sum}</b>
                                        )
                                        :
                                        (
                                        <b>-{el.sum}</b>
                                        )
                                                                     
                                    <p class="text-muted">Спецификация</p>
                                </div>
                            </div>
                        </li>
                    )              
                })                    
                }
            </ul>
        </div>
    )
}
