const defaultState = {
    debet: null,
    credit: null
}
const setCards = "setCards";

export const setCardsAction = (value) => {
    return {type: setCards, value: value}
}

 
export  const CardReducer = (state = defaultState, action = {type: "", value: ""}) => {
    switch (action.type) {
        case setCards:
            return {...state, cards:action.value}
        default:
            return state
    }
}
