const defaultState = {
    client: null
}
const setClient = "setClient";

export const setClientAction = (value) => {
    return {type: setClient, value: value}
}

 
export  const ClientReducer = (state = defaultState, action = {type: "", value: ""}) => {
    switch (action.type) {
        case setClient:
            return {...state, client: action.value}
        default:
            return state
    }
}
